import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/Assets/Signup/Login';
import Signup from './components/Assets/Signup/Signup';
import Home from './components/Assets/Signup/Home'; // Create this component

const App = () => {
  const [user, setUser ] = React.useState(null);

  const handleLogin = (userData) => {
    setUser (userData);
  };

  const handleLogout = () => {
    setUser (null);
  };

  return (
    <Router>
      <Switch>
        <Route path="/" exact>
          <Login onLogin={handleLogin} />
        </Route>
        <Route path="/signup" exact>
          <Signup />
        </Route>
        <Route path="/home" exact>
          {user ? <Home user={user} onLogout={handleLogout} /> : <Redirect to="/" />}
        </Route>
      </Switch>
    </Router>
  );
};

export default App;
